$timeout = [datetime]::Now.AddMinutes( 5 );
$exe = "$env:LOCALAPPDATA\Microsoft\WindowsApps\winget.exe";

while( $true ) {
    if( $exe | Test-Path ) {
        & $exe install --exact twpayne.chezmoi --silent --accept-package-agreements --accept-source-agreements --source winget;
        return;
    }
    if( [datetime]::Now -gt $timeout ) {
        'File {0} does not exist.' -f $exe | Write-Warning;
        return;
    }
    Start-Sleep -Seconds 1;
}

$key = 'HKLM:\SYSTEM\CurrentControlSet\Control\Session Manager\Environment';

$val = (Get-ItemProperty $key -ErrorAction SilentlyContinue).path;

if ($val) {
    Remove-ItemProperty -Path $key -Name 'path' -ErrorAction SilentlyContinue;
    New-ItemProperty -Path $key -Name 'Path' -Value $val -PropertyType ExpandString -Force;
}

foreach( $drive in [System.IO.DriveInfo]::GetDrives() ) {
    if( $found = Join-Path -Path $drive.RootDirectory -ChildPath 'unattend*.ps1' -Resolve -ErrorAction 'SilentlyContinue' ) {
        Start-Process powershell.exe -ArgumentList "-NoExit", "-File $found" 
        return;
    }
}
'Cannot find any files that match pattern.' | Write-Warning;

